DELIMITER ;;
CREATE PROCEDURE `GetAppointmentList`(userId varchar(20), filteredAppointmentId int(11))
BEGIN
	SELECT DISTINCT a.id AS appointmentId,
        a.eventName,
        a.meetingDate,
        a.hostId,
	    u.fullName AS hostName,
	    a.statusId,
	    ast.label AS eventStatus,
	    a.appointmentStore,
        s.storeName,
	    s.storeAddress AS meetingPlace,
        IF(a.hostId = userId, 'sent', 'received') as type,
        IF(a.hostId = userId, '', am.label) AS memberStatus,
	    am1.invitedNumber
    FROM appointments a
    JOIN (
        SELECT am1.memberId, am1.appointmentId, am1.status, mt.label
        FROM appointment_members am1
        JOIN member_status mt ON mt.id = am1.status
        GROUP BY am1.memberId, am1.appointmentId, am1.status
    ) AS am ON a.id = am.appointmentId
    join (
        select
               am.appointmentId,
               count(distinct am.memberId) as invitedNumber
        from appointment_members am
        group by am.appointmentId
    ) as am1 on a.id = am1.appointmentId
    JOIN users u ON a.hostId = u.userId
    LEFT JOIN stores s ON a.appointmentStore = s.storeId
    JOIN appointment_status ast ON a.statusId = ast.id
    WHERE (
        CASE
            WHEN filteredAppointmentId IS NOT NULL THEN a.id = filteredAppointmentId
            ELSE true
        END
    ) AND (a.hostId = userId OR am.memberId = userId);
END ;;
DELIMITER ;

